document.addEventListener('DOMContentLoaded',()=>{const resumeElements=document.getElementsByTagName('resume');if(resumeElements.length>0){const resumeElement=resumeElements[0];const resumeContent=resumeElement.textContent;const convertMarkdownToHtml=(text)=>{text=text.replace(/\*\*(.*?)\*\*/g,'<strong>$1</strong>');text=text.replace(/\*(.*?)\*/g,'<em>$1</em>');text=text.replace(/\[(.*?)\]\((.*?)\)/g,'<a href="$2">$1</a>');return text};const lines=resumeContent.split('\n').map(line=>line.trim()).filter(line=>line);let html='';if(lines.length>=3){const line3=lines[2];const parts=line3.split(' - ').map(part=>part.trim());const convertedParts=parts.map(part=>convertMarkdownToHtml(part)).join(' - ');html+=`<h1>${lines[0]}</h1>`;html+=`<h2>${lines[1]}</h2>`;html+=`<h3>${convertedParts}</h3>`;html+='<hr>'}
let inList=!1;let section='';for(let i=3;i<lines.length;i++){if(lines[i]===''){continue}
let convertedLine=convertMarkdownToHtml(lines[i]);if(convertedLine.trim().startsWith('-')){if(!inList){html+='<ul>';inList=!0}
html+=`<li style="margin-left: 20px;">${convertedLine.substr(convertedLine.indexOf('-') + 1).trim()}</li>`}else{if(inList){html+='</ul>';inList=!1}
if(convertedLine.toLowerCase().includes('summary')){html+='<h3>Summary</h3>';html+=`<p>${convertMarkdownToHtml(lines[i + 1])}</p>`;html+='<hr>';i++;section='summary'}else if(convertedLine.toLowerCase().includes('skill')){html+='<h3>Skills</h3>';const skills=lines[i+1].split(',').map(skill=>convertMarkdownToHtml(skill.trim())).join(' | ');html+=`<p>${skills}</p>`;html+='<hr>';i++;section='skills'}else if(convertedLine.toLowerCase().includes('experience')){html+='<h3>Experience</h3>';section='experience'}else if(convertedLine.toLowerCase().includes('education')){html+='<h3>Education</h3>';html+=`<p>${convertMarkdownToHtml(lines[i + 1])}</p>`;html+='<hr>';i++;section='education'}else if(convertedLine.toLowerCase().includes('certification')){html+='<h3>Certifications</h3>';section='certification'}else{if(section==='certification'){html+=`<p>${convertedLine}</p>`}else{html+=`<p>${convertedLine}</p>`}}}}
if(inList){html+='</ul>'}
resumeElement.innerHTML=html}else{console.error('No <resume> element found.')}})
